export const Product = ({ product, id, cart }) => {
  const onAdd = () => {
    new cart(id).add_to_cart(product);
  };
  const onRemove = () => {
    new cart(id).remove_from_cart(product);
  };
  return (
    <>
      <div>
        {product.name}, {product.price}
        <button onClick={onAdd}> Add </button>
        <button onClick={onRemove}> remove </button>
      </div>
    </>
  );
};
export default Product;
