import { useEffect, useState } from "react";
import "./App.css";
import Product from "./Product";

function App() {
  const API_URL = "http://127.0.0.1:8000";

  useEffect(() => {
    const prods = ProductState.GET();
    const purch = PurchaseState.GET();
    return () => {
      // console.log(purch);
      // console.log(prods);
    };
  }, []);


  const [purchases, setPurchases] = useState([]);
  class PurchaseState {
    constructor(purchase) {
      this.purchase = purchase;
    }
    static objects() {
      return purchases;
    }
    static async GET() {
      setPurchases([]);
      const res = await fetch(API_URL + "/api/purchase/");
      const data = await res.json();
      setPurchases(data);
    }
    async POST() {
      const res = await fetch(API_URL + "/api/purchase/", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(this.purchase),
      });
      const data = await res.json();
      setPurchases([...purchases, data]);
    }
    async PUT() {
      const res = await fetch(API_URL + `/api/purchase/${this.purchase.id}`, {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(this.purchase),
      });
      const data = await res.json();
      setPurchases(
        purchases.map((purchase_from_map) =>
          this.purchase.id === purchase_from_map.id ? data : purchase_from_map
        )
      );
    }
    async DELETE() {
      await fetch(API_URL + `/api/purchase/${this.purchase.id}`, {
        method: "DELETE",
        headers: {
          "Content-type": "application/json",
        },
      });
    }
  }

  const [cart, setCart] = useState([]);
  class Cart {
    constructor(object) {
      this.object = object;
    }
    add_to_cart(object) {
      setCart([...cart, object]);
    }
    remove_from_cart(object) {
      if (cart.length <= 1) {
        setCart([]);
      } else {
        setCart([cart.map((item) => item.id !== object.id && item)]);
      }
    }
    static buy_cart() {
      const counts = {};
      for (const item of cart) {
        counts[item.id] = counts[item.id] ? counts[item.id] + 1 : 1;
      }
      const orders = Object.entries(counts).map(([key, value]) => ({
        quantity: value,
        product: key,
      }));
      console.log({ orders, buyer: 1, payed: false });
      new PurchaseState({ orders, buyer: 1, payed: false }).POST();
    }
  }
  return (
    <div className="columns is-centered is-multiline">
      <div className="column card is-3">
        Total:
        {cart?.map((product) => (
          <Product
            key={
              "cart product" + product.id + Math.floor(Math.random() * (24 + 1))
            }
            id={"product" + product.id + Math.random()}
            product={product}
            cart={Cart}
          />
        ))}
        <button
          onClick={() => {
            Cart.buy_cart();
          }}
        >
          Pay
        </button>
      </div>
      {ProductState?.objects()?.map((product) => (
        <div className="card column is-2">
          <div className="card-content">
            <Product
              key={"product" + product.id + Math.random()}
              id={"product" + product.id + Math.random()}
              product={product}
              cart={Cart}
            />
          </div>
        </div>
      ))}
    </div>
  );
}

export default App;
