import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const API_URL = "http://127.0.0.1:8000";

  useEffect(() => {
    ProductState.GET();

    // return () => {};
  }, []);


  const [products, setProducts] = useState();
  class ProductState {
    constructor(product) {
      this.product = product;
    }
    static async GET() {
      setProducts([]);
      const res = await fetch(API_URL + "/api/product/");
      const data = await res.json();
      setProducts([...products, data]);
    }
    async POST() {
      const res = await fetch(API_URL + "/api/product/", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(this.product),
      });
      const data = await res.json();
      setProducts([...products, data]);
    }
    async PUT() {
      const res = await fetch(API_URL + `/api/product/${this.product.id}`, {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(this.product),
      });
      const data = await res.json();
      setProducts(
        products.map((product_from_map) =>
          this.product.id === product_from_map.id ? data : product_from_map
        )
      );
    }
    async DELETE() {
      await fetch(API_URL + `/api/product/${this.product.id}`, {
        method: "DELETE",
        headers: {
          "Content-type": "application/json",
        },
      });
    }
  }
  return <>
  <div className="card">
    <dic className="card-content"></dic>
  </div>

  </>;
}

export default App;
