import { useEffect, useState } from "react";
import "./App.css";
import Product from "./Product";

function App() {
  const API_URL = "http://127.0.0.1:8000";

  useEffect(() => {
    const prods = ProductState.GET();
    const purch = PurchaseState.GET();
    return () => {
      // console.log(purch);
      // console.log(prods);
    };
  }, []);

  const [products, setProducts] = useState([]);
  class ProductState {
    constructor(product) {
      this.product = product;
    }
    static objects() {
      return products;
    }
    static async GET() {
      setProducts([]);
      const res = await fetch(API_URL + "/api/product/");
      const data = await res.json();
      setProducts(data);
    }
    async POST() {
      const res = await fetch(API_URL + "/api/product/", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(this.product),
      });
      const data = await res.json();
      setProducts([...products, data]);
    }
    async PUT() {
      const res = await fetch(API_URL + `/api/product/${this.product.id}`, {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(this.product),
      });
      const data = await res.json();
      setProducts(
        products.map((product_from_map) =>
          this.product.id === product_from_map.id ? data : product_from_map
        )
      );
    }
    async DELETE() {
      await fetch(API_URL + `/api/product/${this.product.id}`, {
        method: "DELETE",
        headers: {
          "Content-type": "application/json",
        },
      });
    }
  }

  const [purchases, setPurchases] = useState([]);
  class PurchaseState {
    constructor(purchase) {
      this.purchase = purchase;
    }
    static objects() {
      return purchases;
    }
    static async GET() {
      setPurchases([]);
      const res = await fetch(API_URL + "/api/purchase/");
      const data = await res.json();
      setPurchases(data);
    }
    async POST() {
      const res = await fetch(API_URL + "/api/purchase/", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(this.purchase),
      });
      const data = await res.json();
      setPurchases([...purchases, data]);
    }
    async PUT() {
      const res = await fetch(API_URL + `/api/purchase/${this.purchase.id}`, {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(this.purchase),
      });
      const data = await res.json();
      setPurchases(
        purchases.map((purchase_from_map) =>
          this.purchase.id === purchase_from_map.id ? data : purchase_from_map
        )
      );
    }
    async DELETE() {
      await fetch(API_URL + `/api/purchase/${this.purchase.id}`, {
        method: "DELETE",
        headers: {
          "Content-type": "application/json",
        },
      });
    }
  }

  const [cart, setCart] = useState([]);
  class Cart {
    constructor() {}
    add_to_cart(object) {
      setCart([...cart, object]);
      console.log(cart);
    }
    remove_from_cart(object) {
      setCart(cart.map((item) => item.id !== object.id && item));
      console.log(cart);
    }
    buy_cart() {
      console.log(cart);
      // ProductState({}).POST()
    }
  }
  return (
    <div className="columns is-centered">
      <div className="column card  is-3">Total:{cart?.map(product => <Product key={"product" + product.id} product={product} cart={Cart} />)}</div>
      {ProductState?.objects()?.map((product) => (
        <Product key={"product" + product.id} product={product} cart={Cart} />
      ))}
    </div>
  );
}

export default App;
