import "./App.css";

function App() {
  A
  const [products, setProducts] = useState();
  class ProductState {
    constructor(product) {
      this.product = product;
    }
    static async GET() {
      setProducts([]);
      const res = await fetch(API_URL + "/api/product/");
      const data = await res.json();
      setProducts([...products, data]);
    }
    async POST() {
      const res = await fetch(API_URL + "/api/product/", {
        method: "POST",
        headers: { "Content-type": "application/json" },
        body: JSON.stringify(this.product),
      });
      const data = await res.json();
      setProducts([...products, data]);
    }
    async PUT() {
      const res = await fetch(API_URL + `/api/product/${this.product.id}`, {
        method: "PUT",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify(this.product),
      });
      const data = await res.json();
      setProducts(
        products.map((product_from_map) =>
          this.product.id === product_from_map.id ? data : product_from_map
        )
      );
    }
    async DELETE() {
      await fetch(API_URL + `/api/product/${this.product.id}`, {
        method: "DELETE",
        headers: {
          "Content-type": "application/json",
        },
      });
    }
  }
  return <></>;
}

export default App;
