import { useContext } from "react";
import CartContext from "../../context/CartContext";
import Product from "./Product";

export const Cart = ({}) => {
    const {cart} = useContext(CartContext)
  return (<>
    <div className="column card is-3">
        Total:
        {cart?.map((product) => (
          <Product
            key={
              "cart product" + product.id + Math.floor(Math.random() * (24 + 1))
            }
            id={"product" + product.id + Math.random()}
            product={product}
          />
        ))}
        <button
          onClick={() => {
            Cart.buy_cart();
          }}
        >
          Pay
        </button>
      </div>
  </>)
}
export default Cart
